// Generated by Copilot
import {
  ApiImage,
  deleteImage,
  getImages,
  uploadImage,
} from "@/components/AnnotationCanvas/api";
import { ToastType } from "@/components/Toast";
import { useCallback, useEffect, useState } from "react";
import {
  formatImageForDisplay,
  parseApiError,
  validateImageFile,
} from "./imageUtils";

interface UseImagesResult {
  images: ApiImage[];
  isLoading: boolean;
  isError: string;
  pageLength: number;
  page: number;
  selectedImage: number;
  loadNextPage: () => void;
  selectImage: (image: ApiImage, index: number) => void;
  handleUploadImage: (file: File) => Promise<void>;
  handleDeleteImage: (imageId: number, imageName: string) => Promise<boolean>;
}

export function useImages(
  done: boolean = false,
  addToast: (message: string, type: ToastType) => void,
  setImg: (img: ApiImage) => void
): UseImagesResult {
  const [isError, setIsError] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(NaN);
  const [images, setImages] = useState<ApiImage[]>([]);
  const [page, setPage] = useState(1);
  const [pageLength, setPageLength] = useState(NaN);

  const loadNextPage = useCallback(() => {
    setIsLoading(true);
    setTimeout(() => {
      setPage((prev) => prev + 1);
    }, 200);
  }, []);

  const selectImage = useCallback(
    (imgData: ApiImage, index: number) => {
      setSelectedImage(index);
      setImg(imgData);
    },
    [setImg]
  );

  // Load images on component mount and when page changes
  useEffect(() => {
    setIsLoading(true);
    getImages(page, done)
      .then((res) => {
        if (res.total !== pageLength) setPageLength(res.total);
        setImages((prev) => [
          // For page 1, replace existing images, for others append
          ...(page === 1 ? [] : prev),
          ...res.images.map((item) => formatImageForDisplay(item)),
        ]);
        setIsLoading(false);
      })
      .catch((error) => {
        setIsError(error.message || "Failed to load images");
        setIsLoading(false);
      });
  }, [page, done, pageLength]);

  const handleUploadImage = useCallback(
    async (file: File) => {
      // Validate the file
      const validationError = validateImageFile(file);
      if (validationError) {
        addToast(validationError, "error");
        return;
      }

      try {
        setIsLoading(true);

        // Display initial upload toast
        addToast(`Uploading ${file.name}...`, "info");

        // convert the image to a base64 string
        const FR = new FileReader();
        FR.readAsDataURL(file);
        FR.onload = async () => {
          const base64Image = FR.result as string;

          // to ApiImage
          let Image: ApiImage = {
            id: NaN,
            filename: file.name,
            // base64 encoded image data
            src: base64Image,
            is_done: false,
          };

          // Upload the image using the API
          Image = await uploadImage(Image);

          // Add the new image to the beginning of the list
          setImages((prev) => [Image, ...prev]);
          setPageLength((prev) => prev + 1);

          // Show a success toast
          addToast(
            `Image "${Image.filename.split(".")[0]}" uploaded successfully`,
            "success"
          );
        };
      } catch (error) {
        console.error("Error uploading image:", error);
        addToast(parseApiError(error), "error");
      } finally {
        setIsLoading(false);
      }
    },
    [addToast, setImages, setPageLength, setIsLoading]
  );

  const handleDeleteImage = useCallback(
    async (imageId: number, imageName: string) => {
      try {
        // Call the deleteImage API
        await deleteImage(imageId);

        // After successful deletion
        setImages((prevImages) =>
          prevImages.filter((img) => img.id !== imageId)
        );
        setPageLength((prev) => prev - 1);

        // Show success notification
        addToast(`Image "${imageName}" deleted successfully`, "success");
        return true;
      } catch (error) {
        console.error("Error deleting image:", error);
        addToast(parseApiError(error), "error");
        return false;
      }
    },
    [addToast]
  );

  return {
    images,
    isLoading,
    isError,
    pageLength,
    page,
    selectedImage,
    loadNextPage,
    selectImage,
    handleUploadImage,
    handleDeleteImage,
  };
}
