import { ApiImage } from "@/lib/api";
import React from "react";

interface ImageItemProps {
  image: ApiImage;
  isSelected: boolean;
  onSelect: () => void;
}

export default function ImageItem({
  image,
  isSelected,
  onSelect,
}: ImageItemProps) {
  return (
    <li
      className={isSelected ? "active" : ""}
      onClick={onSelect}
      data-image-id={image.id}
    >
      <div className="image-container">
        <div className="image-wrapper">
          <div className="image-placeholder"></div>
          <img
            src={image.src}
            alt={image.filename}
            loading="lazy"
            onLoad={(e) => {
              // Add loaded class to handle fade in effect
              (e.target as HTMLImageElement).classList.add("loaded");
            }}
          />
          {image.is_done && (
            <div className="image-status-indicator">
              <img
                src="/svgs/checkmark.svg"
                alt="Completed"
                width={14}
                height={14}
              />
            </div>
          )}
        </div>
        <p className="image-filename" title={image.filename}>
          {image.filename.split(".")[0]}
        </p>
      </div>
    </li>
  );
}
