export type Point = { x: number; y: number };
// export type Mode = "draw" | "erase";
// Generated by Copilot
export const modes = ["draw", "erase", "hand"] as const;
export type Modes = (typeof modes)[number];

export const ModesLabels: Record<Modes, string> = {
  draw: "Draw tool to mark regions on the image",
  erase: "Erase tool to remove marked regions",
  hand: "Hand tool to pan and move around the image",
};

export type Shape = {
  points: Point[];
  mode: Modes;
};

export type Mask = HTMLImageElement;

export type SaveSatues = {
  isSaving: boolean;
  isModified: boolean;
  isMarkingDone: boolean;
};

export type Tab = {
  feature_id: number;
  name: string;
  isRename: boolean;
};

export type MaskArray = Uint8Array[];
